mapper.select:
select 
    id, member_id, payment_price, point, CONVERT_TZ(regdate, "+0:00", "-9:00")as regdate 
from payment
where id > 0 and member_id = #{memberId}
order by id desc
limit #{cri.pageStart},#{cri.pagesize}

mapper.update:
update payment set
payment_price = paymentPrice + #{point}
where member_id = #{vo.id}
