mapper.select:

select
* from book
where genre = #{keyword}
order by view_count desc
limit #{pageStart},#{pagesize}


select count(id)
from book
where genre = #{keyword}

mapper.update:
update book set title = #{title}, genre = #{genre},
writer = #{writer}, publisher = #{publisher}
, content = #{content}, image = #{image}
where id = #{id}
