mapper.select:
select login_id from
member
where login_id = #{loginId}

select * from member
where
id = #{id} and password = #{password}

select * from member
where
login_id = #{loginId} and jumin = #{jumin}

mapper.update:
update member set
password = #{member.password}, jumin = #{member.jumin},
nickname = #{member.nickname}, phone = #{member.phone},
address = #{member.address}, email = #{member.email}
where id = #{party.id}

update member set
point = point - #{point.totalAmount}
where id = #{memberId}

update member set
point = point + #{point}
where id = #{memberId}

update member set
point = point + #{point}
where id = #{vo.id}

update member set 
	point = point + #{totalAmount}
where id = #{own.memberId}

update member set 
	point = point + #{totalAmount}
where id = #{memberId}

update member set 
	password = #{password}
where id = #{id}
